    componentDidMount() {
        buttonElement = document.getElementById(buttonId);

        buttonElement.addEventListener('click', () => {
            fetch('/weatherforecast')
                .then((response) => {
                    let jsonContent = response.json();
                    this.setState({ response: jsonContent });
                });
        })
    }

    render() {
        return (
            <>
                <TestButton />
                <p>{this.state.response}</p>
            </>
        );
    } 
}

export default App;
